import React, { useState, useEffect, useRef } from 'react';
import { Scissors, TrendingUp, MessageSquare, Sparkles, Clock, Waveform } from 'lucide-react';

export const HeroVideoAnimation = () => {
  const [animationState, setAnimationState] = useState('raw'); // raw, suggestions, editing, final, loop
  const videoRef = useRef(null);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const timeline = [
      { state: 'raw', duration: 3000 },
      { state: 'suggestions', duration: 4000 },
      { state: 'editing', duration: 3500 },
      { state: 'final', duration: 3000 },
      { state: 'loop', duration: 1500 }
    ];

    let currentIndex = 0;
    let startTime = Date.now();

    const animate = () => {
      const currentPhase = timeline[currentIndex];
      const elapsed = Date.now() - startTime;
      const phaseProgress = Math.min(elapsed / currentPhase.duration, 1);

      setProgress(phaseProgress);

      if (elapsed >= currentPhase.duration) {
        currentIndex = (currentIndex + 1) % timeline.length;
        startTime = Date.now();
        setAnimationState(timeline[currentIndex].state);
      }

      requestAnimationFrame(animate);
    };

    const animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  return (
    <div className="relative w-full max-w-lg mx-auto">
      {/* Phone Mockup Container */}
      <div className="relative">
        {/* Phone Frame */}
        <div className="relative bg-card rounded-[2.5rem] p-3 shadow-2xl border-[12px] border-card">
          {/* Notch */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 w-32 h-6 bg-card rounded-b-3xl z-10"></div>

          {/* Screen Container */}
          <div className="relative bg-background rounded-[2rem] overflow-hidden aspect-[9/16]">
            {/* Video */}
            <video
              ref={videoRef}
              className="absolute inset-0 w-full h-full object-cover"
              src="https://customer-assets.emergentagent.com/job_codejourney-24/artifacts/zxy1d9qm_IMG_1358.MOV"
              autoPlay
              loop
              muted
              playsInline
            />

            {/* Overlay Effects */}
            <div className="absolute inset-0 pointer-events-none">
              {/* Raw State - Timeline at bottom */}
              {(animationState === 'raw' || animationState === 'loop') && (
                <div 
                  className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/60 to-transparent p-4 animate-fade-in"
                  style={{
                    animation: animationState === 'loop' ? 'fade-out 1.5s ease-out' : 'fade-in 0.8s ease-out'
                  }}
                >
                  <div className="flex items-center gap-2 text-white/80 text-xs mb-2">
                    <Clock className="w-3 h-3" />
                    <span className="font-sans">0:00 / 0:15</span>
                  </div>
                  <div className="w-full h-1 bg-white/20 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-white rounded-full transition-all duration-300"
                      style={{ width: `${progress * 100}%` }}
                    />
                  </div>
                </div>
              )}

              {/* Suggestions State */}
              {animationState === 'suggestions' && (
                <div className="absolute inset-0 animate-fade-in">
                  {/* Timeline with markers */}
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4">
                    {/* Suggested cuts markers */}
                    <div className="relative mb-3">
                      <div className="flex items-center gap-2 mb-2">
                        <Scissors className="w-4 h-4 text-accent" />
                        <span className="text-white text-xs font-sans font-medium">Suggested cuts</span>
                      </div>
                      <div className="relative w-full h-2 bg-white/20 rounded-full overflow-hidden">
                        <div className="absolute left-[15%] top-0 w-1 h-full bg-accent shadow-glow-neon" />
                        <div className="absolute left-[35%] top-0 w-1 h-full bg-accent shadow-glow-neon" />
                        <div className="absolute left-[68%] top-0 w-1 h-full bg-accent shadow-glow-neon" />
                      </div>
                    </div>
                  </div>

                  {/* Hook Moment tooltip - slides in from top */}
                  <div 
                    className="absolute top-20 left-4 right-4 glass-card p-3 rounded-xl border border-primary/30 shadow-lg"
                    style={{
                      animation: 'slide-down 0.6s ease-out',
                      animationDelay: '0.5s',
                      animationFillMode: 'backwards'
                    }}
                  >
                    <div className="flex items-start gap-2">
                      <div className="w-8 h-8 rounded-lg bg-primary/20 flex items-center justify-center flex-shrink-0">
                        <Sparkles className="w-4 h-4 text-primary" />
                      </div>
                      <div>
                        <p className="text-foreground text-sm font-sans font-semibold">Hook Moment Detected</p>
                        <p className="text-muted-foreground text-xs font-sans">at 0:02 - Strong opening</p>
                      </div>
                    </div>
                  </div>

                  {/* Trending sound match - slides in from right */}
                  <div 
                    className="absolute top-40 left-4 right-4 glass-card p-3 rounded-xl border border-secondary/30 shadow-lg"
                    style={{
                      animation: 'slide-in-right 0.6s ease-out',
                      animationDelay: '1.2s',
                      animationFillMode: 'backwards'
                    }}
                  >
                    <div className="flex items-start gap-2">
                      <div className="w-8 h-8 rounded-lg bg-secondary/20 flex items-center justify-center flex-shrink-0">
                        <TrendingUp className="w-4 h-4 text-secondary" />
                      </div>
                      <div>
                        <p className="text-foreground text-sm font-sans font-semibold">Trending Sound Match</p>
                        <p className="text-muted-foreground text-xs font-sans">Upbeat energetic track</p>
                      </div>
                    </div>
                  </div>

                  {/* Suggested caption - appears inline */}
                  <div 
                    className="absolute top-60 left-4 right-4 glass-card p-3 rounded-xl border border-accent/30 shadow-lg"
                    style={{
                      animation: 'fade-in 0.6s ease-out',
                      animationDelay: '1.8s',
                      animationFillMode: 'backwards'
                    }}
                  >
                    <div className="flex items-start gap-2">
                      <div className="w-8 h-8 rounded-lg bg-accent/20 flex items-center justify-center flex-shrink-0">
                        <MessageSquare className="w-4 h-4 text-accent-foreground" />
                      </div>
                      <div>
                        <p className="text-foreground text-sm font-sans font-semibold">Suggested Caption</p>
                        <p className="text-muted-foreground text-xs font-sans">"POV: When it finally works ðŸ¤¯"</p>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Editing State - Show timeline markers glowing */}
              {animationState === 'editing' && (
                <div className="absolute inset-0 animate-fade-in">
                  {/* Text captions appearing */}
                  <div 
                    className="absolute top-1/2 left-4 right-4 -translate-y-1/2 text-center"
                    style={{
                      animation: 'scale-in 0.4s ease-out',
                      animationDelay: '0.3s',
                      animationFillMode: 'backwards'
                    }}
                  >
                    <div className="inline-block bg-white/95 backdrop-blur-sm px-6 py-3 rounded-2xl shadow-xl border-2 border-primary">
                      <p className="text-foreground text-lg font-sans font-bold">When it finally works ðŸ¤¯</p>
                    </div>
                  </div>

                  {/* Timeline with glowing markers */}
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4">
                    <div className="flex items-center gap-2 mb-2 text-white">
                      <div className="w-2 h-2 bg-primary rounded-full animate-pulse-glow" />
                      <span className="text-xs font-sans">Editing in progress</span>
                    </div>
                    <div className="relative w-full h-2 bg-white/20 rounded-full overflow-hidden">
                      <div className="absolute inset-0 bg-gradient-to-r from-primary via-secondary to-accent opacity-50 animate-shimmer" />
                      <div className="absolute left-[15%] top-0 w-2 h-full bg-primary rounded-full shadow-glow animate-pulse-glow" />
                      <div className="absolute left-[35%] top-0 w-2 h-full bg-secondary rounded-full shadow-glow animate-pulse-glow" style={{ animationDelay: '0.2s' }} />
                      <div className="absolute left-[68%] top-0 w-2 h-full bg-accent rounded-full shadow-glow-neon animate-pulse-glow" style={{ animationDelay: '0.4s' }} />
                    </div>
                  </div>

                  {/* Music waveform visualization */}
                  <div 
                    className="absolute top-4 left-4 right-4 glass-card p-2 rounded-xl border border-white/20"
                    style={{
                      animation: 'fade-in 0.6s ease-out',
                      animationDelay: '0.8s',
                      animationFillMode: 'backwards'
                    }}
                  >
                    <div className="flex items-center gap-2">
                      <Waveform className="w-4 h-4 text-white" />
                      <div className="flex-1 flex items-end gap-1 h-6">
                        {[3, 7, 4, 8, 5, 9, 6, 4, 7, 3, 5, 8, 4, 6, 3, 7, 5, 4, 6, 8].map((height, i) => (
                          <div
                            key={i}
                            className="flex-1 bg-primary/60 rounded-full transition-all duration-200"
                            style={{
                              height: `${height * 8}%`,
                              animation: `wave-pulse 0.8s ease-in-out infinite`,
                              animationDelay: `${i * 0.05}s`
                            }}
                          />
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Final State - Clean result */}
              {animationState === 'final' && (
                <div 
                  className="absolute inset-0 animate-fade-in"
                  style={{
                    animation: 'fade-in 0.8s ease-out'
                  }}
                >
                  {/* Success badge */}
                  <div className="absolute top-4 left-1/2 -translate-x-1/2 glass-card px-4 py-2 rounded-full border border-primary/30 shadow-lg">
                    <div className="flex items-center gap-2">
                      <div className="w-2 h-2 bg-primary rounded-full" />
                      <span className="text-foreground text-xs font-sans font-semibold">Optimized for viral</span>
                    </div>
                  </div>

                  {/* Clean timeline */}
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/60 to-transparent p-4">
                    <div className="w-full h-1 bg-white/20 rounded-full overflow-hidden">
                      <div 
                        className="h-full bg-gradient-to-r from-primary to-secondary rounded-full"
                        style={{ width: `${progress * 100}%` }}
                      />
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Glow effect behind phone */}
        <div className="absolute -inset-4 bg-gradient-to-br from-primary/20 via-secondary/10 to-accent/20 blur-3xl -z-10 animate-pulse-glow" />
      </div>

      {/* Phase indicator dots */}
      <div className="flex justify-center gap-2 mt-6">
        {['raw', 'suggestions', 'editing', 'final'].map((state) => (
          <div
            key={state}
            className={`w-2 h-2 rounded-full transition-all duration-300 ${
              animationState === state
                ? 'bg-primary w-8'
                : 'bg-muted-foreground/30'
            }`}
          />
        ))}
      </div>
    </div>
  );
};
